"use strict";

var BaseCacheEngine = require("./BaseCacheEngine.js");

class RunTimeDriver extends BaseCacheEngine {
  /**
   * Set the storage engine
   */
  storage = this;
  /**
   * Data list
   */
  data = {};
  /**
   * Get item
   */
  getItem(key) {
    return this.data[key];
  }
  /**
   * Set item
   */
  setItem(key, value) {
    this.data[key] = value;
  }
  /**
   * Remove item
   */
  removeItem(key) {
    delete this.data[key];
  }
  /**
   * {@inheritDoc}
   */
  convertValue(value) {
    return value;
  }
  /**
   * {@inheritDoc}
   */
  parseValue(value) {
    return value;
  }
}

module.exports = RunTimeDriver;
