import { getCacheConfig } from "../config.js";
import PlainLocalStorageDriver from "./PlainLocalStorageDriver.js";

class EncryptedLocalStorageDriver extends PlainLocalStorageDriver {
  /**
   * Set data into storage engine
   */
  set(key, value) {
    this.storage.setItem(
      this.getKey(key),
      getCacheConfig("encryption")?.encrypt(value)
    );
    return this;
  }
  /**
   * Get vale from storage engine
   */
  get(key, defaultValue = null) {
    let value = this.storage.getItem(this.getKey(key));
    return value ? getCacheConfig("encryption")?.decrypt(value) : defaultValue;
  }
  /**
   * Remove key from storage
   */
  remove(key) {
    this.storage.removeItem(this.getKey(key));
    return this;
  }
}

export { EncryptedLocalStorageDriver as default };
